package simulation;

public class Address {
	private int x;
	private int y;
	public Address() {
		// TODO Auto-generated constructor stub
	}
	
	public int getX() {
		return x;
	}
	
	public void setX(int x) {
		this.x = x;
	}
	
	public int getY() {
		return y;
	}
	public void setY(int y) {
		this.y = y;
	}
	public Address(int x, int y){
		this.x=x;
		this.y=y;
	}
}
